import React, { useState } from "react";
import "./pages.css";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth, db } from "../FirebaseConfig.js";
import { setDoc, doc } from "firebase/firestore";

function RegisterPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const togglePasswordVisibility = () => {
    setIsPasswordVisible((prev) => !prev);
  };
  const register = () => {
    if (email === "" || password === "" || phone === "") {
      window.alert(
        "Invalid Details",
        "Please enter all the credentials",
        [
          {
            text: "cancel",
            onPress: () => {},
            style: "cancel",
          },
          { text: "ok", onPress: () => {} },
        ],
        { cancelable: false }
      );
    }
    setIsLoading(true);
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredentials) => {
        const user = userCredentials._tokenResponse.email;
        const uid = auth.currentUser.uid;
        setDoc(doc(db, "users", `${uid}`), {
          email: user,
          phone: phone,
          isAdmin: false,
        });
        localStorage.setItem("isAdmin", "false");
        setIsLoading(false);
        setEmail("");
        setPassword("");
        setPhone("");
      })
      .catch((error) => {
        setIsLoading(false);
        console.log("Registration Error:", error);
      });
  };
  return (
    <div
      style={{
        flex: 1,
        backgroundColor: "white",
        padding: 10,
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
      }}
    >
      <div style={{display:"flex", flexDirection:"column", justifyContent:"center", ali}}>
        <div
          style={{
            alignItems: "center",
            justifyContent: "center",
            marginTop: 150,
          }}
        >
          <p style={{ color: "#003580", fontSize: 17, fontWeight: "700" }}>
            Sign Up
          </p>
          <p style={{ fontSize: 17, fontWeight: "500", marginTop: 15 }}>
            Create an account
          </p>
        </div>
        <div style={{ marginTop: 50 }}>
          <p style={{ fontWeight: "500", fontSize: 17, textAlign:"match-parent"}}>Email</p>
          <input
            value={email}
            onChangeText={(text) => setEmail(text)}
            placeholder="enter your email address"
            style={{
              borderColor: "gray",
              borderWidth: 1,
              marginVertical: 15,
              width: 300,
              borderRadius: 15,
              padding: 10,
            }}
          />
        </div>
        <div style={{ marginTop: 15 }}>
          <p style={{ fontWeight: "500", fontSize: 17 }}>Phone number</p>
          <input
            value={phone}
            onChangeText={(text) => setPhone(text)}
            placeholder="enter your phone number"
            style={{
              borderColor: "gray",
              borderWidth: 1,
              marginVertical: 15,
              width: 300,
              borderRadius: 15,
              padding: 10,
            }}
          />
        </div>
        <div style={{ marginTop: 15 }}>
          <p style={{ fontWeight: "500", fontSize: 17 }}>Password</p>
          <div
            style={{
              flexDirection: "row",
              alignItems: "center",
              borderColor: "gray",
              borderWidth: 1,
              marginVertical: 15,
              width: 300,
              borderRadius: 15,
              padding: 10,
            }}
          >
            <input
              value={password}
              onChangeText={(text) => setPassword(text)}
              secureTextEntry={!isPasswordVisible}
              placeholder="enter your password"
              style={{
                borderColor: "gray",
                borderWidth: 1,
                marginVertical: 15,
                width: 300,
                borderRadius: 15,
                padding: 10,
              }}
            />
            <button onPress={togglePasswordVisibility}>
              {/* <MaterialCommunityIcons
                name={isPasswordVisible ? "eye-off" : "eye"}
                size={24}
                color="gray"
              /> */}
            </button>
          </div>
        </div>
        <button
          onClick={register} // Use "onClick" instead of "onPress" for web
          disabled={isLoading}
          style={{
            // Make the button width 100% of its container
            maxWidth: 100, // Set a maximum width for responsiveness
            backgroundColor: isLoading ? "gray" : "#003580",
            padding: 5,
            borderRadius: 7,
            marginTop: 50,
            marginLeft: "auto",
            marginRight: "auto",

            textAlign: "center", // Center the button text horizontally
          }}
        >
          {isLoading ? (
            <p>loading</p>
          ) : (
            <p
              style={{
                fontSize: 17,
                fontWeight: "bold",
                color: "white",
              }}
            >
              Register
            </p>
          )}
        </button>

        <button
          style={{ marginTop: 20 }}
          //   onPress={() => navigation.navigate("Login")}
        >
          <p style={{ textAlign: "center", color: "gray", fontSize: 17 }}>
            Already have an account? Sign in
          </p>
        </button>
      </div>
    </div>
  );
}

export default RegisterPage;
